import mongoose from 'mongoose';

const NoteSchema = new mongoose.Schema({
    title: {
        type: String,
        required: true
    },
    content: {
        type: String,
        required: true
    },
    idempotencyKey: {
        type: String,
        unique: true,
        sparse: true
    }
});

const Note = mongoose.model('Note', NoteSchema);
export default Note;
